export async function apiTicketsList(
  assignedGroups?: number[],
  assignedTo?: number,
  column?: number,
  createdBy?: number,
  ordering?: string,
  page?: number,
  priority?: "critical" | "high" | "low" | "medium",
  search?: string,
  tags?: number[]
): Promise<PaginatedTicketListList> {
  const response = await axios.get(
    `/api/tickets/${(() => {
      const parts = [
        assignedGroups
          ? "assigned_groups=" +
            assignedGroups.map(String).map(encodeURIComponent).join("&assigned_groups=")
          : null,
        assignedTo ? "assigned_to=" + encodeURIComponent(assignedTo) : null,
        column ? "column=" + encodeURIComponent(column) : null,
        createdBy ? "created_by=" + encodeURIComponent(createdBy) : null,
        ordering ? "ordering=" + encodeURIComponent(ordering) : null,
        page ? "page=" + encodeURIComponent(page) : null,
        priority ? "priority=" + encodeURIComponent(priority) : null,
        search ? "search=" + encodeURIComponent(search) : null,
        tags ? "tags=" + tags.map(String).map(encodeURIComponent).join("&tags=") : null,
      ].filter(Boolean);
      return parts.length > 0 ? "?" + parts.join("&") : "";
    })()}`
  );
  return response.data;
}

